const slides=document.querySelectorAll(".slides img");let slideIndex=0,intervalId=null;function initializeSlider(){slides.length>0&&(slides[slideIndex].classList.add("displaySlide"),intervalId=setInterval(nextSlide,5e3))}function showSlide(index){index>=slides.length?slideIndex=0:index<0&&(slideIndex=slides.length-1),slides.forEach(slide=>{slide.classList.remove("displaySlide")}),slides[slideIndex].classList.add("displaySlide")}function prevSlide(){clearInterval(intervalId),slideIndex--,showSlide(slideIndex)}function nextSlide(){slideIndex++,showSlide(slideIndex)}document.addEventListener("DOMContentLoaded",initializeSlider);